import React from 'react';
import { ScrollView, View, Text, TouchableOpacity } from 'react-native';
import { productions } from '../data/productions';
import {
  Container,
  Card,
} from '../components/StyledComponents';
import { theme } from '../theme';

interface LineDetailScreenProps {
  route: any;
  navigation: any;
}

export const LineDetailScreen: React.FC<LineDetailScreenProps> = ({ route, navigation }) => {
  const { line } = route.params;

  // Filtrar produ√ß√µes da linha espec√≠fica
  const lineProductions = productions.filter(prod => prod.lineId === line.id);
  const activeProductions = lineProductions.filter(prod => prod.status === 'EM PRODUCAO');
  const finishedProductions = lineProductions.filter(prod => prod.status === 'FINALIZADA');

  const ProductionItem = ({ production }: { production: any }) => (
    <Card style={{ marginBottom: 8 }}>
      <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
        <View style={{ flex: 1 }}>
          <View style={{ 
            backgroundColor: production.status === 'EM PRODUCAO' ? theme.colors.success : theme.colors.textSecondary,
            paddingHorizontal: 8,
            paddingVertical: 4,
            borderRadius: 4,
            alignSelf: 'flex-start',
            marginBottom: 8
          }}>
            <Text style={{ 
              color: theme.colors.white,
              fontSize: 12,
              fontWeight: 'bold'
            }}>
              {line.name}
            </Text>
            <Text style={{ 
              color: theme.colors.white,
              fontSize: 10
            }}>
              {production.status}
            </Text>
          </View>

          <Text style={{ fontSize: 14, fontWeight: 'bold', marginBottom: 4 }}>
            C√≥digo: <Text style={{ fontWeight: 'normal' }}>{production.productCode}</Text>
          </Text>

          <Text style={{ fontSize: 14, fontWeight: 'bold', marginBottom: 4 }}>
            Produto: <Text style={{ fontWeight: 'normal' }}>{production.productName}</Text>
          </Text>

          <Text style={{ fontSize: 14, fontWeight: 'bold', marginBottom: 4 }}>
            T√©cnico: <Text style={{ fontWeight: 'normal' }}>{production.technician}</Text>
          </Text>

          <Text style={{ fontSize: 14, fontWeight: 'bold', marginBottom: 4 }}>
            Data in√≠cio: <Text style={{ fontWeight: 'normal' }}>{production.startDate}</Text>
          </Text>

          {production.endDate && (
            <Text style={{ fontSize: 14, fontWeight: 'bold' }}>
              Data final: <Text style={{ fontWeight: 'normal' }}>{production.endDate}</Text>
            </Text>
          )}
        </View>

        <TouchableOpacity>
          <Text style={{ fontSize: 18, color: theme.colors.primary }}>‚Üí</Text>
        </TouchableOpacity>
      </View>
    </Card>
  );

  return (
    <Container>
      {/* Header */}
      <View style={{ 
        backgroundColor: theme.colors.primary,
        paddingHorizontal: 16,
        paddingVertical: 20,
        paddingTop: 50
      }}>
        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
          <TouchableOpacity onPress={() => navigation.goBack()}>
            <Text style={{ color: theme.colors.white, fontSize: 18, marginRight: 16 }}>‚Üê</Text>
          </TouchableOpacity>
          <View>
            <Text style={{ 
              color: theme.colors.white,
              fontSize: 18,
              fontWeight: 'bold'
            }}>
              Produ√ß√£o de linha
            </Text>
            <Text style={{ 
              color: theme.colors.white,
              fontSize: 16
            }}>
              {line.name} {line.company || 'Guaravita'}
            </Text>
          </View>
        </View>
      </View>

      <ScrollView style={{ padding: 16 }}>
        {/* Produ√ß√£o Ativa */}
        {activeProductions.length > 0 && (
          <View style={{ marginBottom: 24 }}>
            <Text style={{ 
              fontSize: 16,
              fontWeight: 'bold',
              marginBottom: 12,
              color: theme.colors.text
            }}>
              EM PRODU√á√ÉO
            </Text>
            {activeProductions.map((production) => (
              <ProductionItem key={production.id} production={production} />
            ))}
          </View>
        )}

        {/* Produ√ß√µes Finalizadas */}
        {finishedProductions.length > 0 && (
          <View>
            <Text style={{ 
              fontSize: 16,
              fontWeight: 'bold',
              marginBottom: 12,
              color: theme.colors.text
            }}>
              FINALIZADAS
            </Text>
            {finishedProductions.map((production) => (
              <ProductionItem key={production.id} production={production} />
            ))}
          </View>
        )}

        {/* Caso n√£o tenha produ√ß√µes */}
        {lineProductions.length === 0 && (
          <View style={{ 
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
            paddingTop: 100
          }}>
            <Text style={{ 
              fontSize: 16,
              color: theme.colors.textSecondary,
              textAlign: 'center'
            }}>
              Nenhuma produ√ß√£o encontrada para esta linha
            </Text>
          </View>
        )}
      </ScrollView>
    </Container>
  );
};

