pcount/src/contexts/AuthContext.tsx.content:
import React, { createContext, useState, useContext, ReactNode, useEffect } from 'react';
import { AuthContextType, User, Contract } from '../types';
import { authService } from '../services/authService';
import { contractService } from '../services/contractService';
import { contractManager } from '../services/api';
import { tokenStorage } from '../services/tokenStorage';
import { users } from '../data/users'; // Fallback para desenvolvimento
import { contracts as mockContracts } from '../data/contracts'; // Fallback para desenvolvimento

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [selectedContract, setSelectedContract] = useState<Contract | null>(null);
  const [contracts, setContracts] = useState<Contract[]>([]);
  const [loading, setLoading] = useState(true); // Inicialmente true para carregamento da sess√£o
  const [error, setError] = useState<string | null>(null);

  // Carrega contratos da API
  const loadContracts = async () => {
    try {
      setLoading(true);
      setError(null);
      const apiContracts = await contractService.getContracts();
      setContracts(apiContracts);
    } catch (err) {
      const mockFallbackEnabled = process.env.EXPO_PUBLIC_ENABLE_MOCK_FALLBACK === 'true' && __DEV__;

      if (mockFallbackEnabled) {
        console.warn('API n√£o dispon√≠vel, usando dados mock:', err);
        setContracts(mockContracts);
        setError(null); // N√£o mostrar erro para usu√°rio em modo desenvolvimento
      } else {
        setError('Erro ao carregar contratos. Verifique sua conex√£o.');
      }
    } finally {
      setLoading(false);
    }
  };

  const login = async (email: string, password: string): Promise<boolean> => {
    try {
      setLoading(true);
      setError(null);

      // Tenta autenticar via API V2 
      const response = await authService.login(email, password);

      if (response.access_token) {
        // V2 API retorna contratos dispon√≠veis ap√≥s login
        if (response.contratos && response.contratos.length > 0) {
          // Converte contratos da API V2 para formato interno
          const apiContracts = response.contratos.map(c => ({
            id: c.contratoId,
            name: c.contratoNomeFantasia || c.contratoRazaoSocial || 'Contrato',
            company: c.contratoRazaoSocial || 'Empresa'
          }));
          console.log('Contratos carregados da API:', apiContracts);
          setContracts(apiContracts);
        }

        // Criar usu√°rio baseado na resposta da API V2
        const firstContract = response.contratos?.[0];
        const user: User = {
          id: firstContract?.usuarioId || '1',
          name: firstContract?.usuarioNome || email,
          email: firstContract?.usuarioEmail || email,
          password: '',
          role: 'admin'
        };

        setCurrentUser(user);
        setIsAuthenticated(true);
        return true;
      } else {
        setError('Credenciais inv√°lidas');
        return false;
      }
    } catch (err: any) {
      // Fallback para desenvolvimento APENAS se habilitado
      const mockFallbackEnabled = process.env.EXPO_PUBLIC_ENABLE_MOCK_FALLBACK === 'true' && __DEV__;

      if (mockFallbackEnabled) {
        console.warn('API de login n√£o dispon√≠vel, usando autentica√ß√£o mock:', err);

        const user = users.find(u => u.email === email && u.password === password);

        if (user) {
          setCurrentUser(user);
          setIsAuthenticated(true);
          // Usar contratos mock em caso de fallback
          console.log('Usando contratos mock:', mockContracts);
          setContracts(mockContracts);
          return true;
        }
      }

      // Tratar erros espec√≠ficos da API V2
      let errorMessage = 'Erro de conex√£o. Tente novamente.';
      if (err?.message?.includes('401') || err?.response?.status === 401) {
        errorMessage = 'Credenciais inv√°lidas';
      } else if (mockFallbackEnabled) {
        errorMessage = 'Credenciais inv√°lidas';
      }

      setError(errorMessage);
      return false;
    } finally {
      setLoading(false);
    }
  };

  const selectContract = async (contract: Contract) => {
    try {
      console.log('Selecionando contrato:', contract);
      setSelectedContract(contract);
      // Atualizar o contrato ativo no gerenciador de contratos para usar nas requisi√ß√µes API
      contractManager.setActiveContractId(contract.id);

      // Persistir o contrato selecionado no AsyncStorage
      await tokenStorage.setContractId(contract.id);
      await tokenStorage.setContractData(contract);

      setError(null);
    } catch (error) {
      console.error('Erro ao salvar contrato selecionado:', error);
      setError('Erro ao salvar contrato. Tente novamente.');
    }
  };

  const logout = async () => {
    try {
      setLoading(true);
      await authService.logout();
      // Limpar todos os dados persistidos (tokens + contrato)
      await tokenStorage.clearAllData();
    } catch (err) {
      console.warn('Erro no logout da API:', err);
      // Mesmo se der erro na API, limpar dados locais
      await tokenStorage.clearAllData();
    } finally {
      setCurrentUser(null);
      setSelectedContract(null);
      // Limpar o contrato ativo no logout
      contractManager.setActiveContractId(null);
      setIsAuthenticated(false);
      setContracts([]);
      setLoading(false);
      setError(null);
    }
  };

  const clearError = () => {
    setError(null);
  };

  // Fun√ß√£o para restaurar sess√£o ao inicializar o app
  const restoreSession = async () => {
    try {
      setLoading(true);

      // Verificar se h√° token v√°lido armazenado
      const hasToken = await tokenStorage.hasToken();
      if (!hasToken) {
        setLoading(false);
        return;
      }

      // Verificar se h√° contrato selecionado
      const storedContractData = await tokenStorage.getContractData();
      const storedContractId = await tokenStorage.getContractId();

      if (hasToken && storedContractData && storedContractId) {
        // Sess√£o completa: token + contrato selecionado
        setIsAuthenticated(true);
        setSelectedContract(storedContractData);
        contractManager.setActiveContractId(storedContractId);

        // Tentar validar o token (opcional)
        try {
          const user = await authService.validateToken();
          if (user) {
            setCurrentUser(user);
          }
        } catch (err) {
          console.warn('Token validation failed, but keeping session:', err);
          // Manter sess√£o mesmo se valida√ß√£o falhar
          setCurrentUser({
            id: '1',
            name: 'Usu√°rio',
            email: 'user@example.com',
            password: '',
            role: 'admin'
          });
        }
      } else if (hasToken) {
        // Apenas token, sem contrato - usu√°rio precisa selecionar contrato
        setIsAuthenticated(true);
        try {
          const user = await authService.validateToken();
          if (user) {
            setCurrentUser(user);
          }
          // Carregar contratos para sele√ß√£o
          await loadContracts();
        } catch (err) {
          console.warn('Token inv√°lido, fazendo logout:', err);
          await logout();
        }
      }
    } catch (error) {
      console.error('Erro ao restaurar sess√£o:', error);
      // Em caso de erro, limpar tudo e for√ßar novo login
      await tokenStorage.clearAllData();
    } finally {
      setLoading(false);
    }
  };

  // Restaurar sess√£o ao inicializar o app
  useEffect(() => {
    restoreSession();
  }, []);

  const value: AuthContextType = {
    isAuthenticated,
    currentUser,
    selectedContract,
    contracts,
    loading,
    error,
    login,
    selectContract,
    logout,
    loadContracts,
    clearError,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

