import { useState, useEffect } from 'react';

// Hook gen√©rico para gerenciar chamadas de API
export function useApi<T>(
  apiCall: () => Promise<T>,
  dependencies: any[] = []
) {
  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      const result = await apiCall();
      setData(result);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Erro desconhecido');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Fazer no-op se alguma depend√™ncia cr√≠tica (como contratoId) for falsy
    const shouldSkip = dependencies.some((dep, index) => {
      // Primeiro par√¢metro geralmente √© contratoId - deve ser uma string v√°lida
      if (index === 0 && (typeof dep !== 'string' || dep.trim() === '')) {
        return true;
      }
      return false;
    });

    if (shouldSkip) {
      setData(null);
      setLoading(false);
      setError(null);
      return;
    }

    fetchData();
  }, dependencies);

  return {
    data,
    loading,
    error,
    refetch: fetchData,
  };
}

// Hook para chamadas manuais (n√£o autom√°ticas)
export function useAsyncOperation<T>() {
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  const execute = async (operation: () => Promise<T>): Promise<T | null> => {
    try {
      setLoading(true);
      setError(null);
      const result = await operation();
      return result;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Erro desconhecido';
      setError(errorMessage);
      return null;
    } finally {
      setLoading(false);
    }
  };

  return {
    execute,
    loading,
    error,
    clearError: () => setError(null),
  };
}

