import React, { useState, useMemo } from 'react';
import { View, Text, ScrollView, Dimensions, TouchableOpacity, Modal, FlatList, useWindowDimensions } from 'react-native';
import { Svg, Rect, Text as SvgText, Line, G } from 'react-native-svg';
import { LinearGradient } from 'expo-linear-gradient';
import { MaterialIcons } from '@expo/vector-icons';
import { useProductionStats, useProductionLines } from '../hooks/useProductions';
import { useAuth } from '../contexts/AuthContext';
import {
  Container,
  Title,
  Card,
  FeaturedCard,
  FeaturedCardTitle,
  FeaturedCardValue,
  FeaturedCardSubtitle,
  FeaturedCardAccent,
  FeaturedCardIcon,
  Button,
  ButtonText,
  Input,
} from '../components/StyledComponents';
import { LoadingSpinner } from '../components/LoadingSpinner';
import { ErrorMessage } from '../components/ErrorMessage';
import { theme } from '../theme';

// Fun√ß√µes para obter valores responsivos baseados na largura
const getResponsivePadding = (width: number) => {
  if (width < 375) return 12;
  if (width < 768) return 16;
  return 20;
};

const getResponsiveCardSpacing = (width: number) => {
  if (width < 375) return theme.spacing.xs / 2;
  if (width < 768) return theme.spacing.xs;
  return theme.spacing.sm;
};

// Componente de cart√£o do Total Produzido com design modernizado
const TotalProducedCard: React.FC<{ 
  value: number; 
  title: string;
  subtitle?: string;
}> = ({ value, title, subtitle }) => {
  const { width } = useWindowDimensions();
  const isSmallScreen = width < 375;
  // Garantir que o valor seja positivo
  const safeValue = Math.max(0, value);

  // Definir os n√≠veis e cores (baseado na imagem de refer√™ncia)
  const levels = [
    { threshold: 4800, color: '#dc2626', label: '4800' },
    { threshold: 6600, color: '#ea580c', label: '6600' },
    { threshold: 8400, color: '#65a30d', label: '8400' },
    { threshold: 10200, color: '#16a34a', label: '10200' },
    { threshold: 13000, color: '#0891b2', label: '13000' }
  ];

  // Determinar qual n√≠vel foi atingido
  const currentLevel = levels.reduce((prev, curr) => 
    safeValue >= curr.threshold ? curr : prev, 
    levels[0]
  );

  return (
    <View style={{
      backgroundColor: '#ffffff',
      borderRadius: theme.borderRadius['2xl'],
      padding: isSmallScreen ? theme.spacing.lg : theme.spacing.xl,
      marginVertical: theme.spacing.md,
      shadowColor: theme.colors.shadow,
      shadowOffset: { width: 0, height: 4 },
      shadowOpacity: 0.15,
      shadowRadius: 12,
      elevation: 6,
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Efeito de gradiente de fundo */}
      <LinearGradient
        colors={['rgba(6, 182, 212, 0.05)', 'rgba(6, 182, 212, 0.02)']}
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0
        }}
      />

      {/* T√≠tulo */}
      <Text style={{
        fontSize: isSmallScreen ? theme.fontSizes.xs : theme.fontSizes.sm,
        fontWeight: '700',
        color: theme.colors.text,
        textAlign: 'center',
        marginBottom: isSmallScreen ? theme.spacing.md : theme.spacing.lg,
        letterSpacing: 1,
        textTransform: 'uppercase'
      }}>
        {title}
      </Text>

      {/* Layout do conte√∫do principal */}
      <View style={{ alignItems: 'center' }}>
        {/* C√≠rculo visual com indicador de progresso */}
        <View style={{
          width: isSmallScreen ? 140 : 180,
          height: isSmallScreen ? 140 : 180,
          borderRadius: isSmallScreen ? 70 : 90,
          backgroundColor: '#f8fafc',
          justifyContent: 'center',
          alignItems: 'center',
          marginBottom: isSmallScreen ? theme.spacing.md : theme.spacing.lg,
          borderWidth: isSmallScreen ? 6 : 8,
          borderColor: currentLevel.color,
          position: 'relative'
        }}>
          {/* Valor principal */}
          <Text 
            adjustsFontSizeToFit
            numberOfLines={1}
            style={{
              fontSize: isSmallScreen ? 28 : 36,
              fontWeight: '900',
              color: theme.colors.text,
              textAlign: 'center',
              paddingHorizontal: theme.spacing.sm
            }}
          >
            {safeValue.toLocaleString('pt-BR')}
          </Text>

