export const DashboardScreen: React.FC = () => {
  const { selectedContract, currentUser, logout } = useAuth();
  const { width } = useWindowDimensions();

  // Breakpoints din√¢micos baseados na largura atual
  const isSmallScreen = width < 375;
  const isMediumScreen = width >= 375 && width < 768;
  const isLargeScreen = width >= 768;

  // Estados para filtros
  // Fun√ß√£o para obter data do Brasil (deve estar antes dos useState)
  const getBrazilDateForInit = () => {
    const now = new Date();
    return new Intl.DateTimeFormat('en-CA', {
      timeZone: 'America/Sao_Paulo',
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    }).format(now);
  };

  const [startDate, setStartDate] = useState(getBrazilDateForInit());
  const [endDate, setEndDate] = useState(getBrazilDateForInit());
  const [selectedLineId, setSelectedLineId] = useState<string | null>(null);

  // Buscar linhas de produ√ß√£o
  const { data: productionLines } = useProductionLines(selectedContract?.id || '');

  // Construir filtros para a API
  const filters = useMemo(() => ({
    startDate,
    endDate,
    lineId: selectedLineId || undefined,
    usuarioId: currentUser?.id,
  }), [startDate, endDate, selectedLineId, currentUser?.id]);

  const handleDateChange = (start: string, end: string) => {
    setStartDate(start);
    setEndDate(end);
  };

  const handleLogout = async () => {
    await logout();
  };

  // Verificar se um contrato foi selecionado e usu√°rio autenticado antes de chamar hooks
  if (!selectedContract) {
    return (
      <Container style={{ padding: 16 }}>
        <ErrorMessage 
          message="Nenhum contrato selecionado. Por favor, fa√ßa login novamente."
        />
      </Container>
    );
  }

  if (!currentUser?.id) {
    return (
      <Container style={{ padding: 16 }}>
        <ErrorMessage 
          message="Usu√°rio n√£o autenticado. Por favor, fa√ßa login novamente."
        />
      </Container>
    );
  }

  // Buscar as estat√≠sticas de produ√ß√£o usando o contrato selecionado e filtros
  const { data: productionStats, loading, error } = useProductionStats(
    selectedContract.id,
    filters
  );

  // Mostrar loading
  if (loading) {
    return (
      <Container style={{ padding: 16 }}>
        <Title>Carregando Dashboard...</Title>
        <LoadingSpinner />
      </Container>
    );
  }

  // Mostrar erro
  if (error) {
    return (
      <Container>
        <ScrollView style={{ padding: 16 }}>
          <Title>Dashboard</Title>
          <ErrorMessage message={error} />
        </ScrollView>
      </Container>
    );
  }

  // Dados padr√£o caso n√£o haja dados da API
  const stats = productionStats || {
    operationHours: '--',
    productiveHours: '--',
    avgProduction: 0,
    totalProduced: 0,
    hourlyProduction: [],
  };

  return (
    <Container>
      <DashboardHeader onLogout={handleLogout} />

      <ScrollView 
        showsVerticalScrollIndicator={false}
        contentContainerStyle={{ 
          paddingBottom: theme.spacing.xl,
          paddingHorizontal: getResponsivePadding(width),
          paddingTop: getResponsivePadding(width)
        }}
      >
        <DateSelector
          startDate={startDate}
          endDate={endDate}
          onDateChange={handleDateChange}
        />

        <ProductionSelector
          selectedLineId={selectedLineId}
          onLineChange={setSelectedLineId}
          lines={productionLines || []}
        />

        <Text style={{ 
          fontSize: isSmallScreen ? 16 : isMediumScreen ? 18 : 20, 
          fontWeight: 'bold', 
          color: theme.colors.text, 
          marginBottom: getResponsivePadding(width),
          textAlign: 'center',
          paddingHorizontal: theme.spacing.sm
        }}>
          Dashboard {selectedLineId ? productionLines?.find(l => l.id === selectedLineId)?.name : ''}
        </Text>

        {/* Cart√µes de estat√≠sticas com layout responsivo real */}
        {isSmallScreen ? (
          // Layout responsivo para telas pequenas: primeiro card full-width, outros dois em linha
          <View style={{ marginBottom: theme.spacing.lg }}>
            {/* Primeiro cart√£o ocupando largura total */}
            <View style={{ marginBottom: theme.spacing.sm }}>
              <StatCard
                title="Hora(s)"
                value={`${stats.operationHours}`}
                subtitle="Opera√ß√£o"
                icon="schedule"
                accentColor="#f59e0b"
              />
            </View>

            {/* Dois cart√µes em linha */}
            <View style={{ 
              flexDirection: 'row'
            }}>
              <View style={{ flex: 1, marginRight: getResponsiveCardSpacing(width) / 2 }}>
                <StatCard
                  title="Hora(s)"
                  value={`${stats.productiveHours}`}
                  subtitle="Produtivas"
                  icon="warning"
                  accentColor="#ef4444"
                />
              </View>
              <View style={{ flex: 1, marginLeft: getResponsiveCardSpacing(width) / 2 }}>
                <StatCard
                  title="Produ√ß√£o"
                  value={`${stats.avgProduction}`}
                  subtitle="M√©dia / Hr"
                  icon="trending-up"
                  accentColor="#06b6d4"
                />
              </View>
            </View>
          </View>
        ) : (
          // Layout horizontal tradicional para telas m√©dias e grandes
          <View style={{ 
            flexDirection: 'row', 
            marginBottom: theme.spacing.lg,
            paddingHorizontal: theme.spacing.xs
          }}>
            <View style={{ flex: 1, marginRight: getResponsiveCardSpacing(width) }}>
              <StatCard
                title="Hora(s)"
                value={`${stats.operationHours}`}
                subtitle="Opera√ß√£o"
                icon="schedule"
                accentColor="#f59e0b"
              />
            </View>

            <View style={{ flex: 1, marginHorizontal: getResponsiveCardSpacing(width) }}>
              <StatCard
                title="Hora(s)"
                value={`${stats.productiveHours}`}
                subtitle="Produtivas"
                icon="warning"
                accentColor="#ef4444"
              />
            </View>

            <View style={{ flex: 1, marginLeft: getResponsiveCardSpacing(width) }}>
              <StatCard
                title="Produ√ß√£o"
                value={`${stats.avgProduction}`}
                subtitle="M√©dia / Hr"
                icon="trending-up"
                accentColor="#06b6d4"
              />
            </View>
          </View>
        )}

        {/* Cart√£o do Total Produzido */}
        <TotalProducedCard
          value={stats.totalProduced}
          title="TOTAL PRODUZIDO"
          subtitle="Unidades produzidas no per√≠odo"
        />

        <View style={{ 
          backgroundColor: '#ffffff',
          borderRadius: theme.borderRadius.xl,
          padding: isSmallScreen ? theme.spacing.md : theme.spacing.lg,
          marginTop: theme.spacing.lg,
          shadowColor: theme.colors.shadow,
          shadowOffset: { width: 0, height: 2 },
          shadowOpacity: 0.1,
          shadowRadius: 8,
          elevation: 3
        }}>
          <Text style={{ 
            fontSize: isSmallScreen ? 12 : 14, 
            fontWeight: 'bold', 
            marginBottom: theme.spacing.md, 
            color: theme.colors.text,
            textAlign: 'center',
            textTransform: 'uppercase',
            letterSpacing: 0.5
          }}>
            TOTAL PRODUZIDO / HORA
          </Text>
          <BarChart data={stats.hourlyProduction} />
          <Text style={{ 
            fontSize: isSmallScreen ? 10 : 11, 
            color: theme.colors.textSecondary, 
            textAlign: 'center', 
            marginTop: theme.spacing.sm,
            paddingHorizontal: theme.spacing.sm
          }}>
            Unidades produzidas por hor√°rio de trabalho
          </Text>
        </View>
      </ScrollView>
    </Container>
  );
};

