import React from 'react';
import { ScrollView, View, Text, TouchableOpacity } from 'react-native';
import { useProductions } from '../hooks/useProductions';
import { useAuth } from '../contexts/AuthContext';
import {
  Container,
  Card,
} from '../components/StyledComponents';
import { LoadingSpinner } from '../components/LoadingSpinner';
import { ErrorMessage } from '../components/ErrorMessage';
import { theme } from '../theme';

interface LineDetailScreenProps {
  route: any;
  navigation: any;
}

export const LineDetailScreen: React.FC<LineDetailScreenProps> = ({ route, navigation }) => {
  const { line } = route.params;
  const { selectedContract } = useAuth();

  // Buscar produ√ß√µes usando o contrato selecionado e filtrar por linha
  // Hook far√° no-op se contratoId for falsy
  const { data: allProductions, loading, error } = useProductions(
    selectedContract?.id || '',
    { lineId: line.id }
  );

  // Verificar se um contrato foi selecionado
  if (!selectedContract) {
    return (
      <Container style={{ padding: 16 }}>
        <ErrorMessage 
          message="Nenhum contrato selecionado. Por favor, fa√ßa login novamente."
        />
      </Container>
    );
  }

  // Mostrar loading
  if (loading) {
    return (
      <Container style={{ padding: 16 }}>
        <LoadingSpinner />
      </Container>
    );
  }

  // Mostrar erro
  if (error) {
    return (
      <Container style={{ padding: 16 }}>
        <ErrorMessage message={error} />
      </Container>
    );
  }

  const lineProductions = allProductions || [];
  const activeProductions = lineProductions.filter(prod => prod.status === 'EM PRODUCAO');
  const finishedProductions = lineProductions.filter(prod => prod.status === 'FINALIZADA');

  const ProductionItem = ({ production }: { production: any }) => (
    <Card style={{ marginBottom: 8 }}>
      <TouchableOpacity
        onPress={() => {
          navigation.navigate('ProductionDetail', {
            production: production,
            line: line
          });
        }}
      >
        <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
          <View style={{ flex: 1 }}>
            <View style={{ 
              backgroundColor: production.status === 'EM PRODUCAO' ? theme.colors.success : theme.colors.textSecondary,
              paddingHorizontal: 8,
              paddingVertical: 4,
              borderRadius: 4,
              alignSelf: 'flex-start',
              marginBottom: 8
            }}>
              <Text style={{ 
                color: theme.colors.white,
                fontSize: 12,
                fontWeight: 'bold'
              }}>
                {line.name}
              </Text>
              <Text style={{ 
                color: theme.colors.white,
                fontSize: 10
              }}>
                {production.status}
              </Text>
            </View>

            <Text style={{ fontSize: 14, fontWeight: 'bold', marginBottom: 4 }}>
              C√≥digo: <Text style={{ fontWeight: 'normal' }}>{production.productCode}</Text>
            </Text>

            <Text style={{ fontSize: 14, fontWeight: 'bold', marginBottom: 4 }}>
              Produto: <Text style={{ fontWeight: 'normal' }}>{production.productName}</Text>
            </Text>

            <Text style={{ fontSize: 14, fontWeight: 'bold', marginBottom: 4 }}>
              T√©cnico: <Text style={{ fontWeight: 'normal' }}>{production.technician}</Text>
            </Text>

            <Text style={{ fontSize: 14, fontWeight: 'bold', marginBottom: 4 }}>
              Data in√≠cio: <Text style={{ fontWeight: 'normal' }}>{production.startDate}</Text>
            </Text>

            {production.endDate && (
              <Text style={{ fontSize: 14, fontWeight: 'bold' }}>
                Data final: <Text style={{ fontWeight: 'normal' }}>{production.endDate}</Text>
              </Text>
            )}
          </View>

          <Text style={{ fontSize: 18, color: theme.colors.primary }}>‚Üí</Text>
        </View>
      </TouchableOpacity>
    </Card>
  );

  return (
    <Container>
      <ScrollView style={{ padding: 16 }}>
        {/* Produ√ß√£o Ativa */}
        {activeProductions.length > 0 && (
          <View style={{ marginBottom: 24 }}>
            <Text style={{ 
              fontSize: 16,
              fontWeight: 'bold',
              marginBottom: 12,
              color: theme.colors.text
            }}>
              EM PRODU√á√ÉO
            </Text>
            {activeProductions.map((production) => (
              <ProductionItem key={production.id} production={production} />
            ))}
          </View>
        )}

        {/* Produ√ß√µes Finalizadas */}
        {finishedProductions.length > 0 && (
          <View>
            <Text style={{ 
              fontSize: 16,
              fontWeight: 'bold',
              marginBottom: 12,
              color: theme.colors.text
            }}>
              FINALIZADAS
            </Text>
            {finishedProductions.map((production) => (
              <ProductionItem key={production.id} production={production} />
            ))}
          </View>
        )}

        {/* Caso n√£o tenha produ√ß√µes */}
        {lineProductions.length === 0 && (
          <View style={{ 
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
            paddingTop: 100
          }}>
            <Text style={{ 
              fontSize: 16,
              color: theme.colors.textSecondary,
              textAlign: 'center'
            }}>
              Nenhuma produ√ß√£o encontrada para esta linha
            </Text>
          </View>
        )}
      </ScrollView>
    </Container>
  );
};

pcount/src/screens/LinesScreen.tsx.content:
import React from 'react';
import { FlatList, View, Text } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import { useProductionLinesWithFallback } from '../hooks/useMockFallback';
import { useAuth } from '../contexts/AuthContext';
import {
  Container,
  Title,
  Card,
  StatusIndicator,
} from '../components/StyledComponents';
import { LoadingSpinner } from '../components/LoadingSpinner';
import { ErrorMessage } from '../components/ErrorMessage';
import { theme } from '../theme';

interface LinesScreenProps {
  navigation: any;
}

export const LinesScreen: React.FC<LinesScreenProps> = ({ navigation }) => {
  const { selectedContract } = useAuth();

  // Buscar as linhas de produ√ß√£o com fallback autom√°tico para mock
  const { data: productionLines, loading, error } = useProductionLinesWithFallback(
    selectedContract?.id || ''
  );

  const getStatusText = (status: string) => {
    switch (status) {
      case 'produzindo': return 'Produzindo';
      case 'aguardando': return 'Aguardando';
      case 'iniciando': return 'Iniciando';
      default: return status;
    }
  };

  // Verificar se um contrato foi selecionado
  if (!selectedContract) {
    return (
      <Container style={{ padding: 16 }}>
        <ErrorMessage 
          message="Nenhum contrato selecionado. Por favor, fa√ßa login novamente."
        />
      </Container>
    );
  }

  // Mostrar loading
  if (loading) {
    return (
      <Container style={{ padding: 16 }}>
        <Title>Carregando Linhas...</Title>
        <LoadingSpinner />
      </Container>
    );
  }

  // Mostrar erro apenas se n√£o houver dados (fallback pode resolver automaticamente)
  if (error && !productionLines) {
    return (
      <Container style={{ padding: 16 }}>
        <Title>Selecione uma Linha</Title>
        <ErrorMessage message={error} />
      </Container>
    );
  }

  // Verificar se existem linhas
  if (!productionLines || productionLines.length === 0) {
    return (
      <Container style={{ padding: 16 }}>
        <Title>Selecione uma Linha</Title>
        <Text style={{ textAlign: 'center', marginTop: 32, color: theme.colors.textSecondary }}>
          Nenhuma linha de produ√ß√£o encontrada para este contrato.
        </Text>
      </Container>
    );
  }

  return (
    <Container style={{ padding: 16 }}>
      <Title>Selecione uma Linha</Title>

      <View style={{ flexDirection: 'row', marginBottom: 16, alignItems: 'center' }}>
        <StatusIndicator status="iniciando" />
        <Text style={{ marginLeft: 8, marginRight: 16 }}>Iniciando</Text>
        <StatusIndicator status="aguardando" />
        <Text style={{ marginLeft: 8, marginRight: 16 }}>Aguardando</Text>
        <StatusIndicator status="produzindo" />
        <Text style={{ marginLeft: 8 }}>Produzindo</Text>
      </View>

      <FlatList
        data={productionLines}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <Card onPress={() => navigation.navigate('LineDetail', { line: item })}>
            <View style={{ paddingVertical: 8 }}>
              {/* Header com nome da linha e status */}
              <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 12 }}>
                <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                  <StatusIndicator status={item.status} />
                  <View style={{
                    backgroundColor: item.status === 'produzindo' ? theme.colors.success : 
                                   item.status === 'iniciando' ? theme.colors.warning : theme.colors.textSecondary,
                    marginLeft: 12,
                    paddingHorizontal: 12,
                    paddingVertical: 6,
