                  ) : null;
                })()}

                {/* Produ√ß√£o e efici√™ncia */}
                <View style={{ flexDirection: 'row', justifyContent: 'space-between', marginTop: 4 }}>
                  {item.capacity !== undefined && item.currentProduction !== undefined && (
                    <Text style={{ 
                      fontSize: 12, 
                      color: theme.colors.text,
                      fontWeight: '600'
                    }}>
