          end={{ x: 1, y: 1 }}
          style={{ flex: 1 }}
        >
          <CurvedOverlay />
          <KeyboardAvoidingView 
            style={{ flex: 1 }} 
            behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
          >
            <ScrollView 
              contentContainerStyle={{ flexGrow: 1 }}
              keyboardShouldPersistTaps="handled"
              showsVerticalScrollIndicator={false}
            >
              <LoginCard>
                <ContentArea>
        {/* Logo Area */}
        <LogoContainer style={{ marginTop: Math.max(screenHeight * 0.08, 60) }}>
          <View style={{ alignItems: 'center', marginBottom: Math.max(screenHeight * 0.03, 24) }}>
            {/* Main PCOUNT Logo */}
            <View style={{ 
              flexDirection: 'row', 
              alignItems: 'center', 
              justifyContent: 'center',
              marginBottom: 8
            }}>
              <Text style={{
                fontSize: Math.min(screenWidth * 0.12, 48),
                fontWeight: '800',
                color: 'rgba(255, 255, 255, 0.95)',
                letterSpacing: 1,
                textShadowColor: 'rgba(0, 0, 0, 0.2)',
                textShadowOffset: { width: 0, height: 2 },
                textShadowRadius: 4
              }}>
                PC
              </Text>
              <View style={{
                width: Math.max(screenWidth * 0.1, 40),
                height: Math.max(screenWidth * 0.1, 40),
                borderRadius: Math.max(screenWidth * 0.05, 20),
                backgroundColor: 'rgba(255, 255, 255, 0.9)',
                alignItems: 'center',
                justifyContent: 'center',
                marginHorizontal: screenWidth * 0.01,
                boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.15)',
                elevation: 6
              }}>
                <MaterialIcons name="visibility" size={Math.max(screenWidth * 0.05, 20)} color="#667eea" />
              </View>
              <Text style={{
                fontSize: Math.min(screenWidth * 0.12, 48),
                fontWeight: '800',
                color: 'rgba(255, 255, 255, 0.95)',
                letterSpacing: 1,
                textShadowColor: 'rgba(0, 0, 0, 0.2)',
                textShadowOffset: { width: 0, height: 2 },
                textShadowRadius: 4
              }}>
                UNT
              </Text>
            </View>
          </View>
        </LogoContainer>

        <FormContainer>
          {/* Form Title */}
          <Text style={{
            fontSize: Math.max(screenWidth * 0.040, 16),
            color: '#ffffff',
            textAlign: 'center',
            marginBottom: Math.max(screenHeight * 0.02, 16),
            fontWeight: '600',
            lineHeight: Math.max(screenWidth * 0.050, 20)
          }}>
            Entre com suas credenciais para{'\n'}acessar o sistema.
          </Text>

          {/* Login Form */}
          <View style={{ width: '100%' }}>
            {/* Email Input */}
            <InputWrapper>
              <View style={{ position: 'relative' }}>
                <InputIconContainer>
                  <MaterialIcons name="email" size={Math.max(screenWidth * 0.055, 22)} color="#667eea" />
                </InputIconContainer>
                <StyledInput
                  ref={emailRef}
                  placeholder="Digite seu e-mail"
                  value={email}
                  onChangeText={setEmail}
                  autoCapitalize="none"
                  keyboardType="email-address"
                  textContentType="emailAddress"
                  autoComplete="email"
                  returnKeyType="next"
                  blurOnSubmit={false}
                  placeholderTextColor="rgba(255, 255, 255, 0.7)"
                  onFocus={() => setEmailFocused(true)}
                  onBlur={() => setEmailFocused(false)}
                  onSubmitEditing={() => passwordRef.current?.focus()}
                  style={emailFocused ? {
                    borderColor: '#667eea',
                    borderWidth: 2,
                    backgroundColor: 'rgba(255, 255, 255, 1)',
                    shadowOpacity: 0.1
                  } : {}}
                />
              </View>
            </InputWrapper>

            {/* Password Input */}
            <InputWrapper>
              <View style={{ position: 'relative' }}>
                <InputIconContainer>
                  <MaterialIcons name="lock" size={Math.max(screenWidth * 0.055, 22)} color="#667eea" />
                </InputIconContainer>
                <StyledInput
                  ref={passwordRef}
                  placeholder="Digite sua senha"
                  value={password}
                  onChangeText={setPassword}
                  secureTextEntry={!showPassword}
                  textContentType="password"
                  autoComplete="current-password"
                  returnKeyType="go"
                  placeholderTextColor="rgba(255, 255, 255, 0.7)"
                  onFocus={() => setPasswordFocused(true)}
                  onBlur={() => setPasswordFocused(false)}
