import React from 'react';
import { ScrollView, View, Text } from 'react-native';
import {
  Container,
  Card,
} from '../components/StyledComponents';
import { theme } from '../theme';

interface ProductionDetailScreenProps {
  route: any;
  navigation: any;
}

export const ProductionDetailScreen: React.FC<ProductionDetailScreenProps> = ({ route }) => {
  const { production, line } = route.params;

  // Fun√ß√£o para formatar datas corretamente
  const formatDate = (dateStr: string) => {
    if (!dateStr) return '-';

    // Verifica se a data j√° est√° no formato brasileiro (DD/MM/YYYY - HH:MM)
    if (dateStr.includes(' - ')) {
      return dateStr.split(' - ')[0];
    }

    // Se for ISO timestamp, converte para formato brasileiro
    try {
      const date = new Date(dateStr);
      if (isNaN(date.getTime())) {
        return dateStr;
      }
      return date.toLocaleDateString('pt-BR');
    } catch {
      return dateStr;
    }
  };

  const formatTime = (dateStr: string) => {
    if (!dateStr) return '-';

    // Verifica se a data j√° est√° no formato brasileiro (DD/MM/YYYY - HH:MM)
    if (dateStr.includes(' - ')) {
      return dateStr.split(' - ')[1];
    }

    // Se for ISO timestamp, converte para formato de hora
    try {
      const date = new Date(dateStr);
      if (isNaN(date.getTime())) {
        return '-';
      }
      return date.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' });
    } catch {
      return '-';
    }
  };

  // Verificar se temos dados reais ou usar demonstrativos
  const hasRealData = production.totalProduced !== undefined && production.totalProduced !== null;
  const totalProduced = hasRealData ? production.totalProduced : 2390;
  const avgPerHour = production.avgPerHour ?? (hasRealData ? 0 : 3.66);
  const avgGeneral = production.avgGeneral ?? 0;

  // Dados de produ√ß√£o por hora - usar os dados da produ√ß√£o se dispon√≠vel, sen√£o usar demonstrativos
  const hasRealHourlyData = production.hourlyData && Array.isArray(production.hourlyData);
  const hourlyData = hasRealHourlyData ? production.hourlyData : [
    80, 1, 0, 0, 0, 1, 128, 138, 140, 155, 142, 130,
    117, 123, 136, 117, 130, 127, 124, 134, 121, 126, 127, 93
  ];

  return (
    <Container>
      <ScrollView style={{ padding: 16 }}>
        <Card style={{ marginBottom: 16 }}>
          <View style={{ marginBottom: 12 }}>
            <View style={{ 
              backgroundColor: production.status === 'EM PRODUCAO' ? theme.colors.success : theme.colors.textSecondary,
              paddingHorizontal: 12,
              paddingVertical: 8,
              borderRadius: 8,
              alignSelf: 'flex-start',
              marginBottom: 16
            }}>
              <Text style={{ 
                color: theme.colors.white,
                fontSize: 14,
                fontWeight: 'bold'
              }}>
                Produ√ß√£o
              </Text>
              <Text style={{ 
                color: theme.colors.white,
                fontSize: 12,
                fontWeight: 'bold'
              }}>
                {line.name} {production.status}
              </Text>
            </View>

            {/* Operador (in√≠cio produ√ß√£o) */}
            <View style={{ 
              backgroundColor: theme.colors.surface,
              padding: 12,
              borderRadius: 8,
              marginBottom: 12
            }}>
              <Text style={{ 
                fontSize: 14,
                fontWeight: 'bold',
                color: theme.colors.text,
                marginBottom: 4
              }}>
                Operador (in√≠cio produ√ß√£o)
              </Text>
              <Text style={{ 
                fontSize: 14,
                color: theme.colors.text
              }}>
                {production.technician}
              </Text>
            </View>

            {/* Linha de produ√ß√£o e C√≥digo da linha */}
            <View style={{ 
              flexDirection: 'row',
              marginBottom: 12
            }}>
              <View style={{ 
                flex: 1,
                backgroundColor: theme.colors.surface,
                padding: 12,
                borderRadius: 8,
                marginRight: 6
              }}>
                <Text style={{ 
                  fontSize: 14,
                  fontWeight: 'bold',
                  color: theme.colors.text,
                  marginBottom: 4
                }}>
                  Linha de produ√ß√£o
                </Text>
                <Text style={{ 
                  fontSize: 14,
                  color: theme.colors.text
                }}>
                  {line.name}
                </Text>
              </View>
              <View style={{ 
                flex: 1,
                backgroundColor: theme.colors.surface,
                padding: 12,
                borderRadius: 8,
                marginLeft: 6
              }}>
                <Text style={{ 
                  fontSize: 14,
                  fontWeight: 'bold',
                  color: theme.colors.text,
                  marginBottom: 4
                }}>
                  C√≥digo da linha
                </Text>
                <Text style={{ 
                  fontSize: 14,
