            {/* Cabe√ßalho dos dados de produ√ß√£o */}
            <View style={{ 
              flexDirection: 'row', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              marginBottom: 8
            }}>
              <Text style={{ 
                fontSize: 14,
                fontWeight: 'bold',
                color: theme.colors.text
              }}>
