              <View style={{ 
                width: '33.33%',
                backgroundColor: theme.colors.surface,
                padding: 12,
                borderRadius: 8,
                marginRight: '1%',
                marginBottom: 8
              }}>
                <Text style={{ 
                  fontSize: 12,
                  fontWeight: 'bold',
                  color: theme.colors.text,
                  marginBottom: 4
                }}>
                  Data prod.
                </Text>
                <Text style={{ 
                  fontSize: 14,
                  color: theme.colors.text
                }}>
                  {formatDate(production.startDate)}
                </Text>
              </View>

              <View style={{ 
                width: '33.33%',
                backgroundColor: theme.colors.surface,
                padding: 12,
                borderRadius: 8,
                marginRight: '1%',
                marginBottom: 8
              }}>
                <Text style={{ 
                  fontSize: 12,
                  fontWeight: 'bold',
                  color: theme.colors.text,
                  marginBottom: 4
                }}>
                  Hora in√≠cio
                </Text>
                <Text style={{ 
                  fontSize: 14,
